import React, { useState } from "react";
import "../App.css";

function AddNote(props) {
  const [note, setNote] = useState({
    title: "",
    notes: "",
  });

  function handleChange(e) {
    // e.preventDefault();
    const [name, value] = e.target;
    setNote((preValue) => {
      return {
        ...preValue,
        [name]: value,
      };
    });
  }
  return (
    <div>
      <form>
        <input
          type="text"
          value={note.title}
          onChange={handleChange}
          name="title"
          placeholder="Title"
          
        />
        <p>
          <textarea
            name="note"
            id="note"
            placeholder="Take a note..."
            value={note.notes}
            onChange={handleChange}
            
          ></textarea>
        </p>
      </form>
    </div>
  );
}

export default AddNote;
