import React, { useState } from "react";
import "../App.css";

function AddNote(props) {
  const [note, setNote] = useState({
    title: "",
    notes: "",
  });

  function handleChange(e) {
    e.preventDefault();
    const {name, value} = e.target;
    setNote((preValue) => {
      return {
        ...preValue,
        [name]: value,
      };
    });
  }
  return (
    <div>
      <form>
        <input
          type="text"
          value={note.title}
          onChange={handleChange}
          name="title"
          placeholder="Title"
        />
        <p>
          <textarea
            name="notes"
            id="note"
            placeholder="Take a note..."
            value={note.notes}
            onChange={handleChange}
          ></textarea>
        </p>
        <button>
          <img
            src="https://www.bing.com/images/search?view=detailV2&ccid=dh2q8DDO&id=F7A8901E9644721CCFCFD33232BDAD695288A03D&thid=OIP.dh2q8DDOLNUP5yit2jTbpAHaH_&mediaurl=https%3a%2f%2fcdn4.vectorstock.com%2fi%2f1000x1000%2f93%2f33%2fpositive-symbol-plus-sign-white-icon-vector-15479333.jpg&exph=1080&expw=1000&q=White+Plus+Icon&simid=608020318562042333&FORM=IRPRST&ck=2EF455CFE686AFE42DBE179AFBFAE955&selectedIndex=0"
            alt="ADD"
          />
        </button>
      </form>
    </div>
  );
}

export default AddNote;
