import React, { useState } from "react";
import "../App.css";
import { IoMdAddCircleOutline } from "react-icons/io";
function AddNote(props) {
  const [note, setNote] = useState({
    title: "",
    notes: "",
  });

  function handleChange(e) {
    e.preventDefault();
    const { name, value } = e.target;
    setNote((preValue) => {
      return {
        ...preValue,
        [name]: value,
      };
    });
  }
  function submit(i) {
    i.preventDefault();
    console.log(i);
  }
  return (
    <div>
      <form>
        <input
          type="text"
          value={note.title}
          onChange={handleChange}
          name="title"
          placeholder="Title"
        />
        <p>
          <textarea
            name="notes"
            id="note"
            placeholder="Take a note..."
            value={note.notes}
            onChange={handleChange}
          ></textarea>
        </p>
        <button onClick={submit}>
          <IoMdAddCircleOutline value={{color:'white', background: 'tra'}}/>
        </button>
      </form>
    </div>
  );
}

export default AddNote;
