import React, { useState } from "react";
import "../App.css";
function AddNote({submit, onAdd}) {
  const [note, setNote] = useState({
    title: "",
    notes: "",
  });

  function handleChange(e) {
    e.preventDefault();
    const { name, value } = e.target;
    setNote((preValue) => {
      return {
        ...preValue,
        [name]: value,
      };
    });
  }
    function submit(event) {
        event.preventDefault();
        onAdd(note);
        setNote(note)
    }
    
  return (
    <div>
      <form>
        <input
          type="text"
          value={note.title}
          onChange={handleChange}
          name="title"
          placeholder="Title"
        />
        <p>
          <textarea
            name="notes"
            id="note"
            placeholder="Take a note..."
            value={note.notes}
            onChange={handleChange}
          ></textarea>
        </p>
        <button onClick={submit}>
          ADD
        </button>
      </form>
    </div>
  );
}

export default AddNote;
